import TodoHeader from './components/TodoHeader.vue';
import TodoMain from './components/TodoMain.vue';
import TodoFooter from './components/TodoFooter.vue';
export default {
  components: {
    TodoHeader,
    TodoMain,
    TodoFooter
  },
  data() {
    return {
      list: [{
        id: 1,
        name: '唱'
      }, {
        id: 2,
        name: '跳'
      }, {
        id: 3,
        name: 'rap'
      }]
    };
  },
  methods: {
    handleClear() {
      this.list = '';
    },
    handleAdd(todoName) {
      this.list.push({
        id: +new Date(),
        name: todoName
      });
    },
    handleDel(id) {
      console.log(id);
    }
  },
  watch: {
    list: {
      deep: true,
      handler(newvalue) {
        localStorage.setItem('list', JSON.stringify(newvalue));
      }
    }
  }
};
