//存放vuex相关代码
import Vue from 'vue'
import Vuex from 'vuex'
Vue.use(Vuex)

const store =new Vuex.Store({
  //严格模式
  strict:true,

  //通过state可以提供数据
  state:{
    title:'大标题',
    count: 100
  },
  //通过mutations可以提供修改数据方法
  mutations:{
    //所有mutations函数第一个参数均为state，参数最多有两个
    addCount(state){
      state.count++
    },
    subCount(state,n){
      state.count-=n
    },
    changeTitle(state){
      state.title='改变啦'
    },
    setCount(state,n){
      state.count=n
    }
  }
})

export default store
