//user 模块
const state={
  userInfo:{
    name:'zs',
    age:18
  },
}
const mutations={
  setUser(state,newUserInfo){
    state.userInfo=newUserInfo
  }
}
const actions ={}
const getters={
  UpperCaseName(state){
    return state.userInfo.name.toUpperCase()
  }
}

export default {
  namespaced:true,
  state,
  mutations,
  actions,
  getters
}
